import React, {useState} from 'react';
import FormTodo from './components/Todos/FormTodo';
import Todos from './components/Todos/Todos';
import TodosObj from './models/todo';
// with typescript in react, we use .tsx below the file
function App() {
  const [todos, setTodos] = useState<Array<TodosObj>>([]);
  // we have type never in here it means that we only can use empty array, to ignore this, we have to define the function
  const DUMMY_TODOS = [
    new TodosObj('Learn React'),
    new TodosObj('Learn Typescript')
  ]
  const addToDoHandler = (todo: string | undefined) =>{
    if(!todo){
      return;
    }
    const newTodo = new TodosObj(todo);
    setTodos(prevState =>{
      return [...prevState, newTodo];
    })
  }
  return (
    <>
      <Todos items={todos}/>
      <FormTodo onAddTodo={addToDoHandler}/>
    </>
  );
}

export default App;
